select

 from wash_order

  
    and order_starttime >= #{starttime,jdbcType=VARCHAR}
  
  
    and order_starttime <= #{endtime,jdbcType=VARCHAR}
  
  
    and order_static = #{orderStatic,jdbcType=VARCHAR}
  


  limit #{pagenumber,jdbcType=INTEGER},
  #{pagesize,jdbcType=INTEGER}

select
order_static,order_ispay,order_number,order_address,order_username,order_userphone,order_starttime,order_endtime,
user_name,order_rankname,order_beforepic,order_endpic,order_allmoney
from
wash_order LEFT JOIN wash_user on wash_order.order_serviceid = wash_user.user_id

  
    and order_id = #{orderId,jdbcType=VARCHAR}
  
  
    and order_number like concat('%',#{orderNumber,jdbcType=VARCHAR},'%')
  
  
    and order_userid = #{orderUserid,jdbcType=VARCHAR}
  
  
    and order_username like concat('%',#{orderUsername,jdbcType=VARCHAR},'%')
  
  
    and order_userphone like concat('%',#{orderUserphone,jdbcType=VARCHAR},'%')
  
  
    and order_address = #{orderAddress,jdbcType=VARCHAR}
  
  
    and order_starttime >= #{starttime,jdbcType=TIMESTAMP}
  
  
    and order_starttime <= #{endtime,jdbcType=TIMESTAMP}
  
  
    and order_endtime = #{orderEndtime,jdbcType=TIMESTAMP}
  
  
    and order_serviceid = #{orderServiceid,jdbcType=VARCHAR}
  
  
    and order_static = #{orderStatic,jdbcType=VARCHAR}
  
  
    and order_rankid = #{orderRankid,jdbcType=VARCHAR}
  
  
    and order_rankname = #{orderRankname,jdbcType=VARCHAR}
  
  
    and order_feeid = #{orderFeeid,jdbcType=VARCHAR}
  
  
    and order_feename = #{orderFeename,jdbcType=VARCHAR}
  
  
    and order_beforepic = #{orderBeforepic,jdbcType=VARCHAR}
  
  
    and order_endpic = #{orderEndpic,jdbcType=VARCHAR}
  
  
    and order_allmoney = #{orderAllmoney,jdbcType=DOUBLE}
  
  
    and order_isreduced = #{orderIsreduced,jdbcType=DOUBLE}
  
  
    and order_paymoney = #{orderPaymoney,jdbcType=DOUBLE}
  
  
    and order_ispay = #{orderIspay,jdbcType=INTEGER}
  
  
    and order_isdel = #{orderIsdel,jdbcType=INTEGER}
  
  
    and order_iseva = #{orderIseva,jdbcType=INTEGER}
  
  
    and order_createtime = #{orderCreatetime,jdbcType=TIMESTAMP}
  
  
    and order_updatetime = #{orderUpdatetime,jdbcType=TIMESTAMP}
  
  
    and order_updatebyuserid = #{orderUpdatebyuserid,jdbcType=VARCHAR}
  
  
    and order_evaluateid = #{orderEvaluateid,jdbcType=VARCHAR}
  
  
